SELECT numero_cargo_directo, fecha_registro, precio_unitario, cantidad, subtotal, iva, total, status, clave_refaccion, (SELECT nombre FROM refaccion WHERE refaccion.clave_refaccion = cargo_directo.clave_refaccion) AS nombre_refaccion, id_proveedor, (SELECT nombre FROM proveedor WHERE proveedor.id_proveedor = cargo_directo.id_proveedor) AS nombre_proveedor, folio, numero_usuario, (SELECT CONCAT(nombre," ",apellidos) FROM usuario WHERE usuario.numero_usuario = cargo_directo.numero_usuario) AS nombre_usuario, numero_orden, (SELECT nombre FROM bodega_reciclaje WHERE bodega_reciclaje.id_bodega = (SELECT id_bodega FROM cargo_bodega WHERE cargo_bodega.numero_cargo_directo = cargo_directo.numero_cargo_directo)) AS nombre_bodega FROM cargo_directo WHERE numero_orden = 1 AND status = TRUE AND numero_cargo_directo IN(SELECT numero_cargo_directo FROM cargo_bodega);