SELECT clave_refaccion, nombre, punto_reorden, maximo, minimo, status, (SELECT nombre FROM familia_refaccion WHERE familia_refaccion.id_familia_refaccion = refaccion.id_familia_refaccion) AS familia_refaccion, (SELECT nombre FROM grupo_refaccion WHERE grupo_refaccion.id_grupo_refaccion = (SELECT id_grupo_refaccion FROM familia_refaccion WHERE familia_refaccion.id_familia_refaccion = refaccion.id_familia_refaccion)) AS grupo_refaccion FROM refaccion WHERE status = TRUE;