SELECT numero_entrada, fecha_registro, cantidad, precio_unitario, subtotal, iva, monto, status, clave_refaccion, (SELECT nombre FROM refaccion WHERE refaccion.clave_refaccion = entrada_almacen.clave_refaccion) AS nombre_refaccion, (SELECT nombre FROM proveedor WHERE proveedor.id_proveedor = entrada_almacen.id_proveedor) AS nombre_proveedor, folio, (SELECT CONCAT(nombre," ",apellidos) FROM usuario WHERE usuario.numero_usuario = entrada_almacen.numero_usuario) AS nombre_usuario FROM entrada_almacen WHERE status = TRUE AND fecha_registro BETWEEN $P{inicio} AND $P{fin};
