SELECT numero_salida, costo, status, cantidad, fecha_registro, clave_refaccion, (SELECT nombre FROM refaccion WHERE refaccion.clave_refaccion = salida_almacen.clave_refaccion) AS nombre_refaccion, numero_usuario, (SELECT CONCAT(nombre," ", apellidos) FROM usuario WHERE usuario.numero_usuario = salida_almacen.numero_usuario) AS nombre_usuario, numero_orden, tipo, (SELECT nombre_beneficiario FROM salida_especial WHERE salida_especial.numero_salida = salida_almacen.numero_salida) AS beneficiario FROM salida_almacen WHERE numero_orden = 1 AND status = TRUE AND numero_salida IN(SELECT numero_salida FROM salida_especial);
