SELECT numero_salida, costo, status, cantidad, fecha_registro, clave_refaccion, (SELECT nombre FROM refaccion WHERE refaccion.clave_refaccion = salida_almacen.clave_refaccion) AS nombre_refaccion, numero_usuario, (SELECT CONCAT(nombre," ",apellidos) FROM usuario WHERE usuario.numero_usuario = salida_almacen.numero_usuario) AS nombre_usuario, numero_orden, tipo, (SELECT CONCAT(numero_operador,"# ",nombre," ",apellidos) FROM operador WHERE numero_operador = (SELECT numero_operador FROM salida_operador WHERE salida_operador.numero_salida = salida_almacen.numero_salida)) AS nombre_operador FROM salida_almacen WHERE numero_orden = 1 AND status = TRUE AND numero_salida IN(SELECT numero_salida FROM salida_operador);
